mapper.select:
SELECT
i.id, i.name, i.desc1, i.img_address AS imgAddress, i.number FROM icon i
RIGHT OUTER JOIN reco_icon r ON i.id = r.icon_id;

SELECT COUNT(*) FROM icon_property_info
WHERE icon_id = #{iconId, jdbcType=INTEGER} AND property_id = #{propertyId, jdbcType=INTEGER}
limit 1;

mapper.update:
UPDATE icon SET value = #{value} + 1
WHERE icon_id = #{iconId, jdbcType=INTEGER}
AND value = #{value, jdbcType=INTEGER};

mapper.delete:
DELETE FROM icon_properties_info WHERE
icon_id = #{iconId, jdbcType=INTEGER} AND
property_id = #{proId, jdbcType=INTEGER} limit 1;
