mapper.insert:
INSERT INTO users VALUES (#{userName}, #{password}, 1);
INSERT INTO user_info VALUES(#{userName}, #{name}, #{phone});
INSERT INTO authorities VALUES (#{userName}, #{authority});

mapper.select:
SELECT u.username, name, phone, authority FROM users u
LEFT OUTER JOIN user_info i ON u.username = i.username
LEFT OUTER JOIN authorities a ON u.username = a.username;
